mapper.update:
UPDATE post_comment
SET reply_count = GREATEST(0, reply_count + #{delta})
WHERE id = #{id}

mapper.select:
SELECT id, post_id, author_id, root_id, parent_id, content, reply_count, created_at, deleted_at
FROM post_comment
WHERE post_id = #{postId}
  AND deleted_at IS NULL
  AND (root_id = id OR root_id IS NULL)

  AND (
    created_at < #{cursorTimestamp}
    OR (created_at = #{cursorTimestamp} AND id < #{cursorId})
  )

ORDER BY created_at DESC, id DESC
LIMIT #{limit}

SELECT id, post_id, author_id, root_id, parent_id, content, reply_count, created_at, deleted_at
FROM post_comment
WHERE post_id = #{postId}
  AND root_id = #{rootId}
  AND deleted_at IS NULL
  AND id != #{rootId}
ORDER BY created_at ASC, id ASC
LIMIT #{previewSize}

SELECT id, post_id, author_id, root_id, parent_id, content, reply_count, created_at, deleted_at
FROM post_comment
WHERE post_id = #{postId}
  AND root_id = #{rootId}
  AND deleted_at IS NULL
  AND id != #{rootId}

  AND (
    created_at > #{cursorTimestamp}
    OR (created_at = #{cursorTimestamp} AND id > #{cursorId})
  )

ORDER BY created_at ASC, id ASC
LIMIT #{limit}
