mapper.select:
SELECT follower_id, followee_id, created_at
FROM user_follow
WHERE follower_id = #{followerId}

SELECT follower_id
FROM user_follow
WHERE followee_id = #{owner}

    AND (created_at < #{cursorCreatedAt}
         OR (created_at = #{cursorCreatedAt} AND follower_id < #{cursorId}))


    AND NOT EXISTS (
        SELECT 1 FROM user_block b
        WHERE (b.blocker_id = #{viewer} AND b.blocked_id = follower_id)
           OR (b.blocker_id = follower_id AND b.blocked_id = #{viewer})
    )

ORDER BY created_at DESC, follower_id DESC
LIMIT #{limit}

SELECT followee_id
FROM user_follow
WHERE follower_id = #{owner}

    AND (created_at < #{cursorCreatedAt}
         OR (created_at = #{cursorCreatedAt} AND followee_id < #{cursorId}))


    AND NOT EXISTS (
        SELECT 1 FROM user_block b
        WHERE (b.blocker_id = #{viewer} AND b.blocked_id = followee_id)
           OR (b.blocker_id = followee_id AND b.blocked_id = #{viewer})
    )

ORDER BY created_at DESC, followee_id DESC
LIMIT #{limit}

SELECT COUNT(1)
FROM user_follow
WHERE follower_id = #{followerId} AND followee_id = #{followeeId}
